\begin{tikzpicture}
	\begin{axis}[
		axis x line = middle,
		axis y line = middle,
		every axis y label/.style={at={(ticklabel cs:1.1)}},
		y label style={at={(axis description cs:.5,1.15)},anchor=north},
		ylabel = {$f(x)$},
		every axis x label/.style= {at ={(ticklabel cs:1)}},
		x label style={at={(axis description cs:1.1,.5)},anchor=east},
		xlabel = {$x$},
		xmin = -8, xmax = 8,
		ymin = -4, ymax = 4,
	]
		\addplot[thick, smooth, domain = -8:-3.1] {-2/(x+3)};
		\addplot[thick, smooth, samples=100,domain= -2.9:8] {-2/(x+3)};
		\coordinate (a) at (-3,4);
		\coordinate (b) at (-3,-4);
	\end{axis}
	\draw[dashed] (a)--(b);
\end{tikzpicture}


