\begin{tikzpicture}
	\begin{axis}[
		axis x line = middle,
		axis y line = middle,
		every axis y label/.style={at={(ticklabel cs:1.1)}},
		y label style={at={(axis description cs:0,1.1)},anchor=north},
		ylabel = {$y$},
		every axis x label/.style= {at ={(ticklabel cs:1)}},
		x label style={at={(axis description cs:1.1,0)},anchor=east},
		xlabel = {$x$},
		xmin=0,xmax=2,
		xtick = \empty,
		ytick = \empty
	]
	\addplot[thick, domain=0:2,samples=50]{x}; 
	\coordinate (up) at (1,.05);
	\coordinate (down) at (1,-.05);
	\coordinate (pt1) at (1,1);
	\coordinate (right) at (.05,1);
	\coordinate (left) at (-.05,1);
	\end{axis}
	\draw (up)--(down) node[below] {$a$};
	\draw (right)--(left) node[left] {$f(a)$};
	\fill (pt1) circle (0.1);
	\draw[dashed] (right)--(pt1);
	\draw[dashed] (up)--(pt1);

\end{tikzpicture}


